import React, { Component } from 'react';
import axios from 'axios';

export default class EditExercise extends Component {
  constructor(props) {
    super(props);

    this.onDelete = this.onDelete.bind(this);

    this.state = {
      exercise: {},
    };
  }

  componentDidMount() {
    axios.get(`http://localhost:3000/exercises/${this.props.match.params.id}`)
      .then((response) => {
        this.setState({
          exercise: response.data,
        });
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  onDelete() {
    axios.delete(`http://localhost:3000/exercises/${this.props.match.params.id}`)
      .then((res) => {
        console.log(res.data);
        window.location = '/'; // Redirect to the exercise list after deletion.
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    const { exercise } = this.state;

    return (
      <div>
        <h3>Exercise Details</h3>
        <p>
          <strong>Username:</strong> {exercise.username}
        </p>
        <p>
          <strong>Description:</strong> {exercise.description}
        </p>
        <p>
          <strong>Duration:</strong> {exercise.duration} minutes
        </p>
        <p>
          <strong>Date:</strong> {new Date(exercise.date).toLocaleDateString()}
        </p>

        <button onClick={this.onDelete} className="btn btn-danger">
          Delete Exercise
        </button>
      </div>
    );
  }
}
